mapper.select:
SELECT *
FROM sys_field
WHERE table_id = #{fid} and delete_time = 0;

SELECT *
FROM sys_field
WHERE id = #{id} and delete_time = 0;

mapper.update:
UPDATE sys_field
SET name=#{field.name},
table_id = #{field.tableId},
type = #{field.type},
is_primary = #{field.isPrimary}
WHERE id = #{field.id} and delete_time = 0;

mapper.delete:
UPDATE sys_field
SET delete_time = current_timestamp()
WHERE id = #{id};

UPDATE sys_field
SET delete_time = current_timestamp()
WHERE table_id = #{fid};
