mapper.select:
SELECT *
FROM sys_index
WHERE table_id = #{fid} and delete_time = 0;

SELECT *
FROM sys_index
WHERE id = #{id}
and delete_time = 0;

SELECT *
FROM sys_index
WHERE table_id = #{fid}
and name = #{name}
and delete_time = 0

mapper.update:
UPDATE sys_index
SET name=#{index.name},
id = #{index.id},
table_id = #{index.tableId},
index_type = #{index.indexType},
fields_id_list = #{index.fieldsIdList},
index_properties = #{index.indexProperties}
WHERE id = #{index.id}
and delete_time = 0;

mapper.delete:
UPDATE sys_index
SET delete_time = current_timestamp()
WHERE id = #{id};

UPDATE sys_index
SET delete_time = current_timestamp()
WHERE table_id = #{fid};
